mapper.select:
select * from ( SELECT r.id, r.mobile, r.relation_name insName, '' className, '' price, r.note, r.deal_status dealStatus, r.create_time createTime FROM reservation_apply r WHERE 1 = 1 AND type = 0 and r.relation_id = #{insId} and r.mobile = #{mobile} and r.deal_status = #{dealStatus} AND DATE_FORMAT(r.create_time,"%Y-%m-%d") = STR_TO_DATE(#{endTime},"%Y-%m-%d") AND DATE_FORMAT(r.create_time,"%Y-%m-%d") BETWEEN STR_TO_DATE(#{startTime},"%Y-%m-%d") AND STR_TO_DATE(#{endTime},"%Y-%m-%d")
UNION
SELECT r.id, r.mobile, ii.name insName, ct.name className, ct.price, r.note, r.deal_status dealStatus, r.create_time createTime FROM reservation_apply r LEFT JOIN ins_class_type ct ON ct.id = r.relation_id LEFT JOIN ins_class_type_relation icr ON ct.id = icr.class_type_id LEFT JOIN institution_info ii on ii.id = icr.ins_id WHERE 1 = 1 AND type = 1 and ii.id = #{insId} and ct.id = #{insClassId} and r.mobile = #{mobile} and r.deal_status = #{dealStatus} AND DATE_FORMAT(r.create_time,"%Y-%m-%d") = STR_TO_DATE(#{endTime},"%Y-%m-%d") AND DATE_FORMAT(r.create_time,"%Y-%m-%d") BETWEEN STR_TO_DATE(#{startTime},"%Y-%m-%d") AND STR_TO_DATE(#{endTime},"%Y-%m-%d")
) c ORDER BY c.createTime DESC limit #{page},#{pageSize}

select r.id,r.mobile,r.relation_name insName,ct.name className,ct.price,r.note,r.type,r.deal_status dealStatus,r.create_time createTime  from reservation_apply r
LEFT JOIN ins_class_type ct on ct.id = r.relation_id and r.type = 1
left join ins_class_type_relation icr on ct.id = icr.class_type_id
LEFT JOIN institution_info ii on ii.id = icr.ins_id and ii.id = #{insId}
where 1=1  and ct.id = #{insClassId}
and r.mobile = #{mobile} 
and r.deal_status = #{dealStatus} 
AND DATE_FORMAT(r.create_time,"%Y-%m-%d") = STR_TO_DATE(#{endTime},"%Y-%m-%d")
AND DATE_FORMAT(r.create_time,"%Y-%m-%d") BETWEEN STR_TO_DATE(#{startTime},"%Y-%m-%d") AND STR_TO_DATE(#{endTime},"%Y-%m-%d")
order by r.create_time desc
limit #{page},#{pageSize}

select count(1) from reservation_apply r
LEFT JOIN ins_class_type ct on ct.id = r.relation_id and r.type = 1
left join ins_class_type_relation icr on ct.id = icr.class_type_id
LEFT JOIN institution_info ii on ii.id = icr.ins_id and ii.id = #{insId}
where 1=1  and ct.id = #{insClassId}
and r.mobile = #{mobile} 
and r.deal_status = #{dealStatus} 
AND DATE_FORMAT(r.create_time,"%Y-%m-%d") = STR_TO_DATE(#{endTime},"%Y-%m-%d")
AND DATE_FORMAT(r.create_time,"%Y-%m-%d") BETWEEN STR_TO_DATE(#{startTime},"%Y-%m-%d") AND STR_TO_DATE(#{endTime},"%Y-%m-%d")

select r.id,r.mobile,r.relation_name insName,ct.name className,ct.price,r.note,r.deal_status dealStatus,r.create_time createTime from reservation_apply r
left join ins_class_type_relation icr on r.relation_id = icr.ins_id
LEFT JOIN ins_class_type ct on ct.id = icr.class_type_id
where 1=1 and type = 0
and r.relation_id = #{insId} 
and r.mobile = #{mobile} 
and r.deal_status = #{dealStatus} 
AND DATE_FORMAT(r.create_time,"%Y-%m-%d") = STR_TO_DATE(#{endTime},"%Y-%m-%d")
AND DATE_FORMAT(r.create_time,"%Y-%m-%d") BETWEEN STR_TO_DATE(#{startTime},"%Y-%m-%d") AND STR_TO_DATE(#{endTime},"%Y-%m-%d")
order by r.create_time desc

select r.id,r.mobile,r.relation_name insName,ct.name className,ct.price,r.note,r.type,r.deal_status dealStatus,r.create_time createTime  from reservation_apply r
LEFT JOIN ins_class_type ct on ct.id = r.relation_id and r.type = 1
left join ins_class_type_relation icr on ct.id = icr.class_type_id
LEFT JOIN institution_info ii on ii.id = icr.ins_id and ii.id = #{insId}
where 1=1  and ct.id = #{insClassId}
and r.mobile = #{mobile} 
and r.deal_status = #{dealStatus} 
AND DATE_FORMAT(r.create_time,"%Y-%m-%d") = STR_TO_DATE(#{endTime},"%Y-%m-%d")
AND DATE_FORMAT(r.create_time,"%Y-%m-%d") BETWEEN STR_TO_DATE(#{startTime},"%Y-%m-%d") AND STR_TO_DATE(#{endTime},"%Y-%m-%d")
order by r.create_time desc
