mapper.update:
UPDATE rise_school_info
school_name=#{schoolName}, enrollment_type=#{enrollmentType}, province_code=#{provinceCode}, city_code=#{cityCode}, district=#{district}, is_top=#{isTop}, is_shalve=#{isShalve}, isHidden=1, isHidden=0, create_time=#{createTime}, update_time=#{updateTime}, detail_address=#{detailAddress}, school_web=#{schoolWeb}, school_fax=#{schoolFax}, bus_road=#{busRoad}, user_id =#{userId}, collect_num =#{collectNum}, base_num =#{baseNum}, top_time =NOW(), top_time =null,
id = #{id}

UPDATE rise_school_info
school_name=#{schoolName}, enrollment_type=#{enrollmentType}, province_code=#{provinceCode}, city_code=#{cityCode}, district=#{district}, is_top=#{isTop}, is_shalve=#{isShalve}, create_time=#{createTime}, update_time=#{updateTime}, detail_address=#{detailAddress}, school_web=#{schoolWeb}, school_fax=#{schoolFax}, bus_road=#{busRoad}, user_id =#{userId}, collect_num =#{collectNum}, base_num =#{baseNum},
id = #{id}

mapper.select:
SELECT rsi.id             AS id,
        rsi.school_name    AS school_name,
        rsi.enrollment_type AS enrollment_type,
        rsi.province_code  AS province_code,
        rsi.city_code      AS city_code,
        rsi.district       AS district,
        rsi.is_top         AS is_top,
        rsi.is_shalve      AS is_shalve,
        rsi.create_time    AS create_time,
        rsi.update_time    AS update_time,
        rsi.detail_address AS detail_address,
        rsi.school_web     AS school_web,
        rsi.school_fax     AS school_fax,
        rsi.bus_road       AS bus_road,
        rsi.user_id       AS user_id,
        rsi.collect_num   AS collect_num,
        rsi.base_num      AS base_num,
        uss.userName AS userName,
        srsdo.item_name AS enrollmentName,
        srsdp.item_name AS provinceName,
        srsdq.item_name AS cityName,
        srsdr.item_name AS districtName,
        rsi.isHidden
  FROM rise_school_info rsi
  INNER JOIN users uss ON uss.id = rsi.user_id
  INNER JOIN sys_rise_school_dict srsdo ON srsdo.item_code = rsi.enrollment_type
  INNER JOIN sys_rise_school_dict srsdp ON srsdp.item_code = rsi.province_code
  INNER JOIN sys_rise_school_dict srsdq ON srsdq.item_code = rsi.city_code
  INNER JOIN sys_rise_school_dict srsdr ON srsdr.item_code = rsi.district
    where 1=1
    and rsi.school_name=#{schoolName}
    and rsi.enrollment_type=#{enrollmentType}
    and rsi.province_code=#{provinceCode}
    and rsi.city_code=#{cityCode}
    and rsi.district=#{district}
    and rsi.is_top=#{isTop}
    and rsi.is_shalve=#{isShalve}
    and rsi.create_time=#{createTime}
    and rsi.update_time=#{updateTime}
    and rsi.detail_address=#{detailAddress}
    and rsi.school_web=#{schoolWeb}
    and rsi.school_fax=#{schoolFax}
    and rsi.bus_road=#{busRoad}
    and rsi.user_id =#{userId}
    and rsi.id =#{id}
ORDER BY rsi.is_top DESC ,top_time desc,rsi.update_time DESC
limit #{firstIndex},#{pageSize}

SELECT id,item_code AS itemCode,item_name AS itemName,item_type AS itemType
  FROM sys_rise_school_dict
 WHERE item_type = #{itemType}
  AND parent_code = #{parentCode}

    SELECT  count(*)
    FROM  rise_school_info
    WHERE 1 = 1
     AND school_name LIKE '%${schoolName}%'
     AND  province_code = #{provinceCode}
     AND city_code = #{cityCode}
    AND district = #{district}
    AND is_shalve = #{isShalve}
    AND DATE_FORMAT(create_time,"%Y-%m-%d") >= STR_TO_DATE(#{startTime},"%Y-%m-%d")
    AND DATE_FORMAT(create_time,"%Y-%m-%d") = STR_TO_DATE(#{endTime},"%Y-%m-%d")
    AND DATE_FORMAT(create_time,"%Y-%m-%d") BETWEEN STR_TO_DATE(#{startTime},"%Y-%m-%d") AND STR_TO_DATE(#{endTime},"%Y-%m-%d")
	AND is_top = #{isTop}
    AND enrollment_type = #{enrollmentType}
